mapper.select:
SELECT TBS.*,TBS.CONT_NAME CONT_NAME2,TSD.DEPT_NAME,TSU.USER_NAME,TBD.DEAL_NAME,TBC.CONT_NAME FROM T_BIZ_SETTLEMENT TBS LEFT JOIN T_SYS_DEPT TSD ON TBS.DEPT_ID=TSD.DEPT_ID LEFT JOIN T_SYS_USER TSU ON TBS.USER_ID=TSU.USER_ID LEFT JOIN T_BIZ_DEAL TBD ON TBS.DEAL_ID=TBD.DEAL_ID LEFT JOIN T_BIZ_CONTRACTOR TBC ON TBC.CONT_ID=TBD.CONTRACT_ID
AND TBS.SETTLE_STATUS = #{settleStatus}
AND TBS.DEAL_ID = #{dealId}
ORDER BY TBS.CREATE_TIME DESC

SELECT * FROM (SELECT TBD.DEAL_TYPE, TB.SETTLE_STATUS, TBD.DEAL_INCOME, TBD.DEPT_ID, TBD.DEAL_VALUE, TB.`SETTLE_AMOUNT`, IFNULL(TBD.DEAL_SETTLEMENT,0) SUM_SETTLE_AMOUNT, TBC2.CONT_NAME, TSD.DEPT_NAME, TSU.`USER_ID`, TSU.USER_NAME, TBD.DEAL_NAME, TBD.DEAL_NO, TB.SETTLE_ID, TBD.DEAL_ID, date_format(TBD.DEAL_START,'%Y-%m-%d') DEAL_START, date_format(TBD.DEAL_END,'%Y-%m-%d') DEAL_END
FROM T_BIZ_SETTLEMENT TB INNER JOIN ( SELECT IFNULL(MIN(TBCS.STEP_NO),-1) STEP_NO,TBS.SETTLE_ID FROM T_BIZ_SETTLEMENT TBS INNER JOIN T_BIZ_DEAL TBD ON TBD.`DEAL_ID`=TBS.`DEAL_ID` INNER JOIN T_BIZ_CHECK_STEP TBCS ON TBCS.`CHECK_OBJ_ID`=TBS.SETTLE_ID INNER JOIN T_BIZ_CHECK_MAN TBCM ON TBCS.`STEP_ID`=TBCM.`STEP_ID` WHERE TBD.`DEAL_STATUS`='progressAuditing' AND TBCM.`CHECK_STATE`='PENDING' AND TBS.`SETTLE_STATUS`='buildAuditing' GROUP BY TBS.SETTLE_ID ) A ON TB.SETTLE_ID=A.SETTLE_ID INNER JOIN ( SELECT MIN(TBCS.`STEP_NO`) STEP_NO,TBCS.CHECK_OBJ_ID FROM T_BIZ_CHECK_STEP TBCS INNER JOIN T_BIZ_CHECK_MAN TBCM ON TBCS.`STEP_ID`=TBCM.`STEP_ID` INNER JOIN T_SYS_USER TSU ON TSU.`USER_ID`=TBCM.`USER_ID` WHERE TSU.`USER_ID`=#{userId} AND TBCM.`CHECK_STATE`='PENDING' GROUP BY TBCS.CHECK_OBJ_ID ) B ON A.STEP_NO=B.STEP_NO AND A.SETTLE_ID=B.CHECK_OBJ_ID LEFT JOIN T_SYS_DEPT TSD ON TB.DEPT_ID=TSD.DEPT_ID LEFT JOIN T_SYS_USER TSU ON TB.USER_ID=TSU.USER_ID LEFT JOIN T_BIZ_DEAL TBD ON TB.DEAL_ID=TBD.DEAL_ID LEFT JOIN T_BIZ_CONTRACTOR TBC2 ON TBC2.CONT_ID=TBD.CONTRACT_ID
AND TBD.DEAL_NO =#{dealNo}
AND TBD.DEAL_NAME like CONCAT('%',#{dealName},'%')
AND TBD.DEAL_VALUE <= #{dealValueMax}
AND TBD.DEAL_VALUE >= #{dealValueMin}
AND TBD.CONTRACT_ID = #{contractId}
AND TBD.DEAL_SIGN_TIME >= str_to_date(#{dealSignTimeStart},'%Y-%m-%d %H:%i:%s')
AND TBD.DEAL_SIGN_TIME <= str_to_date(#{dealSignTimeEnd},'%Y-%m-%d %H:%i:%s')
AND TBD.DEAL_TYPE =#{dealType}
)a
UNION ALL
SELECT * FROM (SELECT TBD.DEAL_TYPE, TB.SETTLE_STATUS, TBD.DEAL_INCOME, TBD.DEPT_ID, TBD.DEAL_VALUE, TB.`SETTLE_AMOUNT`, IFNULL(TBD.DEAL_SETTLEMENT,0) SUM_SETTLE_AMOUNT, TBC2.CONT_NAME, TSD.DEPT_NAME, TSU.`USER_ID`, TSU.USER_NAME, TBD.DEAL_NAME, TBD.DEAL_NO, TB.SETTLE_ID, TBD.DEAL_ID, date_format(TBD.DEAL_START,'%Y-%m-%d') DEAL_START, date_format(TBD.DEAL_END,'%Y-%m-%d') DEAL_END
FROM T_BIZ_SETTLEMENT TB INNER JOIN ( SELECT IFNULL(MIN(TBCS.STEP_NO),-1) STEP_NO,TBS.SETTLE_ID FROM T_BIZ_SETTLEMENT TBS INNER JOIN T_BIZ_DEAL TBD ON TBD.`DEAL_ID`=TBS.`DEAL_ID` INNER JOIN T_BIZ_CHECK_STEP TBCS ON TBCS.`CHECK_OBJ_ID`=TBS.SETTLE_ID INNER JOIN T_BIZ_CHECK_MAN TBCM ON TBCS.`STEP_ID`=TBCM.`STEP_ID` WHERE TBD.`DEAL_STATUS`='progressAuditing' AND TBCM.`CHECK_STATE`='PENDING' AND TBS.`SETTLE_STATUS`='buildAuditing' GROUP BY TBS.SETTLE_ID ) A ON TB.SETTLE_ID=A.SETTLE_ID INNER JOIN ( SELECT MIN(TBCS.`STEP_NO`) STEP_NO,TBCS.CHECK_OBJ_ID FROM T_BIZ_CHECK_STEP TBCS INNER JOIN T_BIZ_CHECK_MAN TBCM ON TBCS.`STEP_ID`=TBCM.`STEP_ID` INNER JOIN T_SYS_USER TSU ON TSU.`USER_ID`=TBCM.`USER_ID` WHERE TSU.`ENTRUST_USER_ID`=#{userId} AND TBCM.`CHECK_STATE`='PENDING' GROUP BY TBCS.CHECK_OBJ_ID ) B ON A.STEP_NO=B.STEP_NO AND A.SETTLE_ID=B.CHECK_OBJ_ID LEFT JOIN T_SYS_DEPT TSD ON TB.DEPT_ID=TSD.DEPT_ID LEFT JOIN T_SYS_USER TSU ON TB.USER_ID=TSU.USER_ID LEFT JOIN T_BIZ_DEAL TBD ON TB.DEAL_ID=TBD.DEAL_ID LEFT JOIN T_BIZ_CONTRACTOR TBC2 ON TBC2.CONT_ID=TBD.CONTRACT_ID
AND TBD.DEAL_NO =#{dealNo}
AND TBD.DEAL_NAME like CONCAT('%',#{dealName},'%')
AND TBD.DEAL_VALUE <= #{dealValueMax}
AND TBD.DEAL_VALUE >= #{dealValueMin}
AND TBD.CONTRACT_ID = #{contractId}
AND TBD.DEAL_SIGN_TIME >= str_to_date(#{dealSignTimeStart},'%Y-%m-%d %H:%i:%s')
AND TBD.DEAL_SIGN_TIME <= str_to_date(#{dealSignTimeEnd},'%Y-%m-%d %H:%i:%s')
AND TBD.DEAL_TYPE =#{dealType}
)b

SELECT TBD.DEAL_TYPE, TB.SETTLE_STATUS, TBD.DEAL_INCOME, TBD.DEPT_ID, TBD.DEAL_VALUE, TB.`SETTLE_AMOUNT`, IFNULL(TBD.DEAL_SETTLEMENT,0) SUM_SETTLE_AMOUNT, TBC2.CONT_NAME, TSD.DEPT_NAME, TSU.`USER_ID`, TSU.USER_NAME, TBD.DEAL_NAME, TBD.DEAL_NO, TB.SETTLE_ID, TBD.DEAL_ID, date_format(TBD.DEAL_START,'%Y-%m-%d') DEAL_START, date_format(TBD.DEAL_END,'%Y-%m-%d') DEAL_END FROM ( SELECT TB1.*,TB2.CHECK_TIME FROM T_BIZ_SETTLEMENT TB1 INNER JOIN ( SELECT TBS.SETTLE_ID,TBCM.CHECK_TIME FROM `T_BIZ_SETTLEMENT` TBS INNER JOIN T_BIZ_CHECK_STEP TBCS ON TBS.`SETTLE_ID`=TBCS.`CHECK_OBJ_ID` INNER JOIN T_BIZ_CHECK_MAN TBCM ON TBCS.`STEP_ID`=TBCM.`STEP_ID` WHERE TBCM.`USER_ID`=#{userId} AND TBCM.`CHECK_STATE`='PENDED' ) TB2 ON TB1.`SETTLE_ID`=TB2.`SETTLE_ID`
) TB
LEFT JOIN T_SYS_DEPT TSD ON TB.DEPT_ID=TSD.DEPT_ID LEFT JOIN T_SYS_USER TSU ON TB.USER_ID=TSU.USER_ID LEFT JOIN T_BIZ_DEAL TBD ON TB.DEAL_ID=TBD.DEAL_ID LEFT JOIN T_BIZ_CONTRACTOR TBC2 ON TBC2.CONT_ID=TBD.CONTRACT_ID
AND TBD.DEAL_NO =#{dealNo}
AND TBD.DEAL_NAME like CONCAT('%',#{dealName},'%')
AND TBD.DEAL_VALUE <= #{dealValueMax}
AND TBD.DEAL_VALUE >= #{dealValueMin}
AND TBD.CONTRACT_ID = #{contractId}
AND TBD.DEAL_SIGN_TIME >= str_to_date(#{dealSignTimeStart},'%Y-%m-%d %H:%i:%s')
AND TBD.DEAL_SIGN_TIME <= str_to_date(#{dealSignTimeEnd},'%Y-%m-%d %H:%i:%s')
AND TBD.DEAL_TYPE =#{dealType}
ORDER BY TB.CHECK_TIME DESC

SELECT TBD.*,TSD.DEPT_NAME, TBC2.CONT_NAME FROM (

SELECT TBD.*,tsu.USER_NAME,tbs.settle_id,tbs.settle_amount,tbs.settle_status  FROM T_BIZ_SETTLEMENT tbs
LEFT JOIN T_BIZ_DEAL TBD ON tbs.deal_id=TBD.deal_id
LEFT JOIN T_SYS_USER tsu ON tbs.USER_ID=tsu.user_id
WHERE tbs.settle_status IN ('buildAuditing','back')) TBD
LEFT JOIN T_SYS_DEPT TSD ON TBD.DEPT_ID=TSD.DEPT_ID
LEFT JOIN T_BIZ_CONTRACTOR TBC2 ON TBC2.CONT_ID=TBD.CONTRACT_ID



        AND TBD.DEAL_NO =#{dealNo}


        AND TBD.DEAL_NAME like  CONCAT('%',#{dealName},'%')



        AND TBD.DEAL_VALUE <= #{dealValueMax}



        AND TBD.DEAL_VALUE >= #{dealValueMin}



        AND TBD.CONTRACT_ID = #{contractId}


        AND TBD.DEAL_SIGN_TIME >= str_to_date(#{dealSignTimeStart},'%Y-%m-%d %H:%i:%s')


        AND TBD.DEAL_SIGN_TIME <= str_to_date(#{dealSignTimeEnd},'%Y-%m-%d %H:%i:%s')



        AND TBD.DEPT_ID=#{dataScopeDept}



        AND TBD.USER_ID  = #{dataScopeSelf}

select bs.SETTLE_AMOUNT from T_BIZ_SETTLEMENT bs

    and DATE_FORMAT(bs.DOWN_TIME,'%Y%m') = #{yearMonth}

        and bs.DEAL_ID = #{dealId}
