mapper.select:
select * from(
SELECT TPP.PROJ_ID,TPP.PROJ_NAME,TPP.DEAL_VALUE,TPP.DEAL_NO,IFNULL(TPP.DEAL_NAME,TPP.PROJ_NAME) DEAL_NAME,TPPR.PROJ_STATUS,TPP.PROJ_PHASE,
TPP.SEL_CONT_TYPE,TPPR.CREATE_AT,TSU.USER_NAME,TSD.DEPT_NAME,TPPR.RESULT_ID,TPP.CREATE_ID,
TPPR.AMOUNT_UNIT,TPPR.RESULT_NOTES,TPPR.PLAN_NO,TPPP.PLAN_ID,TPPP.SEL_CONT_ID,TPRL.CONT_ID,
IF(TPPR.RESULT_ID in (SELECT PURC_ID FROM T_PROJ_PURC_HIDE WHERE PURC_TYPE = 'rePurchase'),1,0) freezeStatus
FROM T_PROJ_PURC_RESULT TPPR
LEFT JOIN T_PROJ_PROJECT TPP ON TPPR.PROJ_ID=TPP.PROJ_ID
LEFT JOIN T_PROJ_PURC_PLAN TPPP  ON TPPR.PLAN_ID=TPPP.PLAN_ID
LEFT JOIN T_SYS_USER TSU ON TPP.CREATE_ID=TSU.USER_ID
LEFT JOIN T_SYS_DEPT TSD ON TPP.OWNER_DEPT_ID=TSD.DEPT_ID
LEFT JOIN (
SELECT MAX(CONT_ID) CONT_ID , RESULT_ID FROM T_PROJ_RESULT_LIST GROUP BY RESULT_ID
) TPRL ON TPPR.RESULT_ID=TPRL.RESULT_ID) t


       and t.freezeStatus = #{freezeStatus}


        and t.PROJ_NAME LIKE CONCAT('%',#{projName},'%')



        and t.DEAL_NO LIKE CONCAT('%',#{dealNo},'%')


        and t.PLAN_NO LIKE CONCAT('%',#{planNo},'%')


        and t.SEL_CONT_TYPE =#{selContType}



        and t.PROJ_ID =#{projId}



        and t.PROJ_STATUS=#{projStatus}



        and t.PROJ_PHASE =#{projPhase}



        and t.USER_NAME LIKE CONCAT('%',#{userName},'%')



        and t.DEPT_NAME LIKE CONCAT('%',#{deptName},'%')



        and t.CREATE_AT>= str_to_date(#{createAtStart},'%Y-%m-%d %H:%i:%s')



        and t.CREATE_AT <= str_to_date(#{createAtEnd},'%Y-%m-%d %H:%i:%s')



    and t.SEL_CONT_TYPE=#{selContType}



        and t.RESULT_ID=#{resultId}




        AND t.DEPT_ID=#{dataScopeDept}



        AND t.USER_ID = #{dataScopeSelf}



ORDER BY t.CREATE_AT DESC

SELECT SUBSTRING_INDEX(TPPR.PLAN_NO,'-',-1)
FROM T_PROJ_PURC_RESULT TPPR
WHERE SUBSTRING(TPPR.PLAN_NO,1,4)=#{year}
AND SUBSTRING(TPPR.PLAN_NO,6,4)=#{projPhaseType}
ORDER BY  CAST(SUBSTRING_INDEX(TPPR.PLAN_NO,'-',-1) AS SIGNED INTEGER) DESC LIMIT 1
